    private void txtInv1KeyTyped(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_txtInv1KeyTyped
        // TODO add your handling code here:
        char c = evt.getKeyChar();

        if (!Character.isDigit(c)) {
            getToolkit().beep();

            evt.consume();
        }
    }//GEN-LAST:event_txtInv1KeyTyped

